import React, { useState } from 'react';
import { Send, Loader2, Sparkles, Clock, Target, Lightbulb } from 'lucide-react';

const TaskForm = ({ onSubmit, isLoading }) => {
  const [taskDescription, setTaskDescription] = useState('');
  const [selectedPriority, setSelectedPriority] = useState('medium');
  const [selectedCategory, setSelectedCategory] = useState('general');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (taskDescription.trim() && !isLoading) {
      onSubmit(taskDescription.trim(), { priority: selectedPriority, category: selectedCategory });
      setTaskDescription('');
    }
  };

  const priorities = [
    { value: 'low', label: 'Low', color: 'text-neutral-600', bg: 'bg-neutral-100' },
    { value: 'medium', label: 'Medium', color: 'text-warning-600', bg: 'bg-warning-100' },
    { value: 'high', label: 'High', color: 'text-error-600', bg: 'bg-error-100' },
    { value: 'urgent', label: 'Urgent', color: 'text-error-700', bg: 'bg-error-200' }
  ];

  const categories = [
    { value: 'general', label: 'General Analysis', icon: Target },
    { value: 'research', label: 'Research & Intelligence', icon: Lightbulb },
    { value: 'data', label: 'Data Processing', icon: Clock },
    { value: 'monitoring', label: 'System Monitoring', icon: Sparkles }
  ];

  const exampleTasks = [
    {
      title: "Market Research Analysis",
      description: "Research the latest trends in artificial intelligence and machine learning for Q1 2024",
      category: "research",
      priority: "high"
    },
    {
      title: "Competitive Intelligence",
      description: "Analyze market data for renewable energy companies and identify key competitors",
      category: "research", 
      priority: "medium"
    },
    {
      title: "Project Planning",
      description: "Create a comprehensive project plan for implementing a new CRM system",
      category: "general",
      priority: "high"
    },
    {
      title: "Data Processing Pipeline",
      description: "Process and analyze customer feedback data from multiple sources",
      category: "data",
      priority: "medium"
    },
    {
      title: "Content Strategy Development",
      description: "Develop a content strategy for social media marketing campaign targeting B2B clients",
      category: "general",
      priority: "low"
    }
  ];

  const handleExampleClick = (example) => {
    setTaskDescription(example.description);
    setSelectedCategory(example.category);
    setSelectedPriority(example.priority);
  };

  const getCharacterCount = () => {
    return taskDescription.length;
  };

  const isNearLimit = () => {
    return getCharacterCount() > 800;
  };

  return (
    <div className="card p-6 space-y-6">
      <div className="flex items-center space-x-3">
        <div className="p-2 bg-brand-100 rounded-lg">
          <Sparkles className="w-5 h-5 text-brand-600" />
        </div>
        <div>
          <h2 className="text-xl font-semibold text-neutral-900">Create New Task</h2>
          <p className="text-sm text-neutral-600">Deploy AI agents to handle complex tasks</p>
        </div>
      </div>

      <form onSubmit={handleSubmit} className="space-y-6">
        {/* Task Description */}
        <div className="space-y-2">
          <label htmlFor="task-description" className="block text-sm font-medium text-neutral-700">
            Task Description
          </label>
          <textarea
            id="task-description"
            rows={4}
            className={`form-textarea ${isNearLimit() ? 'border-warning-300 focus:border-warning-500 focus:ring-warning-500' : ''}`}
            placeholder="Describe the task you want the AI agents to work on. Be specific about your requirements and expected outcomes..."
            value={taskDescription}
            onChange={(e) => setTaskDescription(e.target.value)}
            disabled={isLoading}
            maxLength={1000}
          />
          <div className="flex justify-between items-center">
            <span className={`text-xs ${isNearLimit() ? 'text-warning-600' : 'text-neutral-500'}`}>
              {getCharacterCount()}/1000 characters
            </span>
            {isNearLimit() && (
              <span className="text-xs text-warning-600 font-medium">Approaching character limit</span>
            )}
          </div>
        </div>

        {/* Priority and Category */}
        <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
          <div className="space-y-2">
            <label className="block text-sm font-medium text-neutral-700">Priority</label>
            <div className="grid grid-cols-2 gap-2">
              {priorities.map((priority) => (
                <button
                  key={priority.value}
                  type="button"
                  onClick={() => setSelectedPriority(priority.value)}
                  disabled={isLoading}
                  className={`p-2 rounded-lg text-xs font-medium transition-all ${
                    selectedPriority === priority.value
                      ? `${priority.bg} ${priority.color} ring-2 ring-offset-1 ring-current`
                      : 'bg-neutral-100 text-neutral-600 hover:bg-neutral-200'
                  }`}
                >
                  {priority.label}
                </button>
              ))}
            </div>
          </div>

          <div className="space-y-2">
            <label className="block text-sm font-medium text-neutral-700">Category</label>
            <select
              value={selectedCategory}
              onChange={(e) => setSelectedCategory(e.target.value)}
              disabled={isLoading}
              className="form-input"
            >
              {categories.map((category) => (
                <option key={category.value} value={category.value}>
                  {category.label}
                </option>
              ))}
            </select>
          </div>
        </div>

        {/* Submit Button */}
        <div className="flex justify-end">
          <button
            type="submit"
            disabled={!taskDescription.trim() || isLoading}
            className="btn-primary btn-lg min-w-[140px]"
          >
            {isLoading ? (
              <>
                <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                Processing...
              </>
            ) : (
              <>
                <Send className="w-4 h-4 mr-2" />
                Execute Task
              </>
            )}
          </button>
        </div>
      </form>

      {/* Example Tasks */}
      <div className="border-t border-neutral-200 pt-6">
        <h3 className="text-sm font-medium text-neutral-700 mb-4 flex items-center">
          <Lightbulb className="w-4 h-4 mr-2" />
          Example Tasks
        </h3>
        <div className="space-y-3">
          {exampleTasks.map((example, index) => (
            <button
              key={index}
              onClick={() => handleExampleClick(example)}
              disabled={isLoading}
              className="w-full text-left p-4 bg-neutral-50 hover:bg-neutral-100 rounded-lg transition-colors group"
            >
              <div className="flex items-start justify-between mb-2">
                <h4 className="text-sm font-medium text-neutral-900 group-hover:text-brand-700 transition-colors">
                  {example.title}
                </h4>
                <div className="flex space-x-2">
                  <span className={`px-2 py-1 rounded-full text-2xs font-medium ${
                    priorities.find(p => p.value === example.priority)?.bg
                  } ${priorities.find(p => p.value === example.priority)?.color}`}>
                    {priorities.find(p => p.value === example.priority)?.label}
                  </span>
                </div>
              </div>
              <p className="text-xs text-neutral-600 line-clamp-2">
                {example.description}
              </p>
            </button>
          ))}
        </div>
      </div>
    </div>
  );
};

export default TaskForm;
