import React, { useState, useEffect } from 'react';
import AgentLogsPanel from './AgentLogsPanel';
import AgentManagementPanel from './AgentManagementPanel';
import LiveExecutionWindow from './LiveExecutionWindow';
import ChatBox from './ChatBox';
import Header from './Header';
import Settings from './Settings';
import WelcomeGuide from './WelcomeGuide';

const MainLayout = () => {
  const [agents, setAgents] = useState([]);
  const [tasks, setTasks] = useState([]);
  const [logs, setLogs] = useState([]);
  const [errors, setErrors] = useState([]);
  const [executionData, setExecutionData] = useState(null);
  const [isConnected, setIsConnected] = useState(true);
  const [showSettings, setShowSettings] = useState(false);
  const [showWelcome, setShowWelcome] = useState(false);

  useEffect(() => {
    // Check if user has seen welcome guide
    const hasSeenWelcome = localStorage.getItem('hasSeenWelcome');
    if (!hasSeenWelcome) {
      setShowWelcome(true);
    }

    // Initialize data fetching
    fetchAgents();
    fetchTasks();
    fetchLogs();

    // Set up periodic refresh
    const interval = setInterval(() => {
      fetchAgents();
      fetchTasks();
      fetchLogs();
    }, 10000); // Refresh every 10 seconds

    return () => clearInterval(interval);
  }, []);

  const handleWelcomeClose = () => {
    setShowWelcome(false);
    localStorage.setItem('hasSeenWelcome', 'true');
  };

  const fetchAgents = async () => {
    try {
      const response = await fetch('http://localhost:8000/agents');
      if (response.ok) {
        const data = await response.json();
        setAgents(data.agents || []);
        setIsConnected(true);
      } else {
        setAgents([]);
        setIsConnected(false);
      }
    } catch (error) {
      console.error('Error fetching agents:', error);
      setAgents([]);
      setIsConnected(false);
      setErrors(prev => [...prev, { type: 'fetch', message: 'Failed to fetch agents', timestamp: new Date() }]);
    }
  };

  const fetchTasks = async () => {
    try {
      const response = await fetch('http://localhost:8000/tasks');
      if (response.ok) {
        const data = await response.json();
        setTasks(data.tasks || []);
      } else {
        setTasks([]);
      }
    } catch (error) {
      console.error('Error fetching tasks:', error);
      setTasks([]);
      setErrors(prev => [...prev, { type: 'fetch', message: 'Failed to fetch tasks', timestamp: new Date() }]);
    }
  };

  const fetchLogs = async () => {
    try {
      // Since there's no direct logs endpoint, we'll get logs from tasks
      const response = await fetch('http://localhost:8000/tasks');
      if (response.ok) {
        const data = await response.json();
        const allLogs = [];
        (data.tasks || []).forEach(task => {
          if (task.execution_log) {
            task.execution_log.forEach(log => {
              allLogs.push({
                ...log,
                task_id: task.task_id,
                agent_name: log.agent
              });
            });
          }
        });
        setLogs(allLogs.sort((a, b) => new Date(b.timestamp) - new Date(a.timestamp)));
      } else {
        setLogs([]);
      }
    } catch (error) {
      console.error('Error fetching logs:', error);
      setLogs([]);
      setErrors(prev => [...prev, { type: 'fetch', message: 'Failed to fetch logs', timestamp: new Date() }]);
    }
  };

  const handleChatMessage = async (message) => {
    try {
      const response = await fetch('http://localhost:8000/tasks', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ task_description: message }),
      });
      const data = await response.json();

      // Trigger execution monitoring
      setExecutionData({
        task_id: data.task_id,
        task_name: message,
        agent_name: 'AI Assistant',
        description: 'Processing user request...',
        timestamp: new Date()
      });

      // Refresh data
      setTimeout(() => {
        fetchTasks();
        fetchLogs();
      }, 1000);

      return { message: 'Task created successfully', task_id: data.task_id };
    } catch (error) {
      console.error('Error sending chat message:', error);
      setErrors(prev => [...prev, { type: 'chat', message: 'Failed to send message', timestamp: new Date() }]);
      throw error;
    }
  };

  return (
    <div className="h-screen flex flex-col bg-gray-50">
      {/* Header */}
      <Header 
        isConnected={isConnected}
        onSettingsClick={() => setShowSettings(true)}
      />

      {/* Main Content Area - Adjusted height to account for chat box */}
      <div className="flex-1 flex overflow-hidden" style={{ height: 'calc(100vh - 80px - 200px)' }}>
        {/* Left Panel - Agent Logs and Tasks */}
        <div className="w-1/4 bg-white border-r border-gray-200 flex flex-col">
          <AgentLogsPanel 
            logs={logs} 
            tasks={tasks} 
            onRefresh={() => {
              fetchLogs();
              fetchTasks();
            }}
          />
        </div>

        {/* Center Panel - Live Execution Window */}
        <div className="flex-1 bg-gray-100 flex flex-col">
          <LiveExecutionWindow 
            executionData={executionData}
            onExecutionUpdate={setExecutionData}
          />
        </div>

        {/* Right Panel - Agent Management and Errors */}
        <div className="w-1/4 bg-white border-l border-gray-200 flex flex-col">
          <AgentManagementPanel 
            agents={agents}
            errors={errors}
            onAgentCreate={fetchAgents}
            onErrorClear={() => setErrors([])}
          />
        </div>
      </div>

      {/* Bottom Chat Box - Fixed at bottom */}
      <div className="h-48 bg-white border-t border-gray-200 flex-shrink-0">
        <ChatBox 
          onSendMessage={handleChatMessage}
          onExecutionStart={setExecutionData}
        />
      </div>

      {/* Settings Modal */}
      <Settings 
        isOpen={showSettings}
        onClose={() => setShowSettings(false)}
      />
    </div>
  );
};

export default MainLayout;
