import React, { useState, useEffect } from 'react';
import Header from './components/Header';
import Dashboard from './components/Dashboard';
import AgentCard from './components/AgentCard';
import TaskForm from './components/TaskForm';
import TaskCard from './components/TaskCard';
import TaskModal from './components/TaskModal';
import LiveExecutionMonitor from './components/LiveExecutionMonitor';
import SmartTaskInput from './components/SmartTaskInput';
import AgentTaskManager from './components/AgentTaskManager';
import { apiService, wsService } from './services/api';
import { useWebSocket } from './hooks/useWebSocket';
import { 
  RefreshCw, 
  Users, 
  ListTodo, 
  Activity, 
  BarChart3,
  Settings,
  Bell,
  Filter,
  Search,
  Brain,
  Zap
} from 'lucide-react';

function App() {
  const [agents, setAgents] = useState([]);
  const [tasks, setTasks] = useState([]);
  const [selectedTask, setSelectedTask] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isLiveMonitorOpen, setIsLiveMonitorOpen] = useState(false);
  const [liveMonitorTaskId, setLiveMonitorTaskId] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [isRefreshing, setIsRefreshing] = useState(false);
  const [activeTab, setActiveTab] = useState('dashboard');
  const [taskFilter, setTaskFilter] = useState('all');
  const [searchQuery, setSearchQuery] = useState('');

  const { isConnected, messages } = useWebSocket();

  // Load initial data
  useEffect(() => {
    loadAgents();
    loadTasks();
  }, []);

  // Handle WebSocket messages
  useEffect(() => {
    const latestMessage = messages[messages.length - 1];
    if (latestMessage) {
      switch (latestMessage.type) {
        case 'task_created':
        case 'task_started':
        case 'task_completed':
        case 'task_failed':
          loadTasks();
          break;
        case 'agent_heartbeat':
        case 'agent_statuses':
          if (latestMessage.agents) {
            setAgents(latestMessage.agents);
          }
          break;
        case 'execution_log':
          // Update task progress in real-time
          setTasks(prevTasks => 
            prevTasks.map(task => 
              task.task_id === latestMessage.task_id 
                ? {
                    ...task,
                    progress: latestMessage.task_progress || task.progress,
                    current_agent: latestMessage.current_agent,
                    current_step: latestMessage.current_step
                  }
                : task
            )
          );
          break;
        default:
          break;
      }
    }
  }, [messages]);

  // Set up WebSocket service reference for components
  useEffect(() => {
    window.wsService = wsService;
  }, []);

  const loadAgents = async () => {
    try {
      const response = await apiService.getAgents();
      setAgents(response.data.agents);
    } catch (error) {
      console.error('Error loading agents:', error);
    }
  };

  const loadTasks = async () => {
    try {
      const response = await apiService.getAllTasks();
      setTasks(response.data.tasks.sort((a, b) => 
        new Date(b.created_at) - new Date(a.created_at)
      ));
    } catch (error) {
      console.error('Error loading tasks:', error);
    }
  };

  const handleCreateTask = async (taskDescription, options = {}) => {
    setIsLoading(true);
    try {
      const response = await apiService.createTask(taskDescription);
      // Automatically open live monitor for new running tasks
      setLiveMonitorTaskId(response.data.task_id);
      setIsLiveMonitorOpen(true);
      // Switch to tasks tab to see the new task
      setActiveTab('tasks');
    } catch (error) {
      console.error('Error creating task:', error);
      alert('Failed to create task. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  const handleViewTaskDetails = (task) => {
    setSelectedTask(task);
    setIsModalOpen(true);
  };

  const handleViewLiveExecution = (task) => {
    setLiveMonitorTaskId(task.task_id);
    setIsLiveMonitorOpen(true);
  };

  const handleViewAgentActivity = (agent) => {
    // Find the current task for this agent
    const currentTask = tasks.find(task => 
      task.status === 'running' && task.current_agent === agent.name
    );

    if (currentTask) {
      handleViewLiveExecution(currentTask);
    }
  };

  const handleRefresh = async () => {
    setIsRefreshing(true);
    await Promise.all([loadAgents(), loadTasks()]);
    setIsRefreshing(false);
  };

  const getTaskStats = () => {
    const total = tasks.length;
    const completed = tasks.filter(t => t.status === 'completed').length;
    const running = tasks.filter(t => t.status === 'running').length;
    const failed = tasks.filter(t => t.status === 'failed').length;

    return { total, completed, running, failed };
  };

  const getAgentStats = () => {
    const total = agents.length;
    const ready = agents.filter(a => a.status === 'ready').length;
    const busy = agents.filter(a => a.status === 'busy').length;
    const totalCompleted = agents.reduce((sum, agent) => sum + (agent.total_tasks_completed || 0), 0);

    return { total, ready, busy, totalCompleted };
  };

  const getFilteredTasks = () => {
    let filtered = tasks;

    // Apply status filter
    if (taskFilter !== 'all') {
      filtered = filtered.filter(task => task.status === taskFilter);
    }

    // Apply search filter
    if (searchQuery) {
      filtered = filtered.filter(task => 
        task.task_id.toLowerCase().includes(searchQuery.toLowerCase()) ||
        (task.result && task.result.toLowerCase().includes(searchQuery.toLowerCase()))
      );
    }

    return filtered;
  };

  const getRecentActivity = () => {
    return tasks
      .filter(task => task.execution_log && task.execution_log.length > 0)
      .flatMap(task => 
        task.execution_log.slice(-3).map(log => ({
          ...log,
          task_id: task.task_id
        }))
      )
      .sort((a, b) => new Date(b.timestamp) - new Date(a.timestamp))
      .slice(0, 10);
  };

  const stats = getTaskStats();
  const agentStats = getAgentStats();
  const filteredTasks = getFilteredTasks();
  const recentActivity = getRecentActivity();

  const tabs = [
    { id: 'dashboard', label: 'Dashboard', icon: BarChart3 },
    { id: 'create', label: 'AI Orchestrator', icon: Brain },
    { id: 'manager', label: 'Agent & Task Manager', icon: Zap },
    { id: 'tasks', label: 'Task Monitor', icon: ListTodo },
    { id: 'agents', label: 'Agent Status', icon: Users }
  ];

  const taskFilters = [
    { value: 'all', label: 'All Tasks' },
    { value: 'running', label: 'Running' },
    { value: 'completed', label: 'Completed' },
    { value: 'failed', label: 'Failed' }
  ];

  return (
    <div className="min-h-screen bg-neutral-50">
      <Header 
        isConnected={isConnected}
        onSettingsClick={() => console.log('Settings clicked')}
        onNotificationsClick={() => console.log('Notifications clicked')}
      />

      {/* Navigation Tabs */}
      <div className="bg-white border-b border-neutral-200 sticky top-16 z-30">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex items-center justify-between">
            <nav className="flex space-x-8">
              {tabs.map((tab) => {
                const Icon = tab.icon;
                return (
                  <button
                    key={tab.id}
                    onClick={() => setActiveTab(tab.id)}
                    className={`flex items-center space-x-2 py-4 px-1 border-b-2 font-medium text-sm transition-colors ${
                      activeTab === tab.id
                        ? 'border-brand-500 text-brand-600'
                        : 'border-transparent text-neutral-500 hover:text-neutral-700 hover:border-neutral-300'
                    }`}
                  >
                    <Icon className="w-4 h-4" />
                    <span>{tab.label}</span>
                  </button>
                );
              })}
            </nav>

            <div className="flex items-center space-x-3">
              <button
                onClick={handleRefresh}
                disabled={isRefreshing}
                className="btn-ghost btn-sm"
                title="Refresh data"
              >
                <RefreshCw className={`w-4 h-4 ${isRefreshing ? 'animate-spin' : ''}`} />
              </button>
            </div>
          </div>
        </div>
      </div>

      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Dashboard Tab */}
        {activeTab === 'dashboard' && (
          <Dashboard 
            stats={stats}
            agentStats={agentStats}
            recentActivity={recentActivity}
          />
        )}

        {/* Tasks Tab */}
        {activeTab === 'tasks' && (
          <div className="space-y-6">
            {/* Filters and Search */}
            <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between space-y-4 sm:space-y-0">
              <div>
                <h1 className="text-2xl font-bold text-neutral-900">Task Management</h1>
                <p className="text-neutral-600 mt-1">Monitor and manage AI agent tasks</p>
              </div>

              <div className="flex items-center space-x-4">
                {/* Search */}
                <div className="relative">
                  <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-neutral-400" />
                  <input
                    type="text"
                    placeholder="Search tasks..."
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                    className="form-input pl-10 w-64"
                  />
                </div>

                {/* Filter */}
                <div className="flex items-center space-x-2">
                  <Filter className="w-4 h-4 text-neutral-400" />
                  <select
                    value={taskFilter}
                    onChange={(e) => setTaskFilter(e.target.value)}
                    className="form-input"
                  >
                    {taskFilters.map(filter => (
                      <option key={filter.value} value={filter.value}>
                        {filter.label}
                      </option>
                    ))}
                  </select>
                </div>
              </div>
            </div>

            {/* Task Grid */}
            {filteredTasks.length === 0 ? (
              <div className="card p-12 text-center">
                <ListTodo className="w-16 h-16 text-neutral-400 mx-auto mb-4" />
                <h3 className="text-lg font-medium text-neutral-900 mb-2">
                  {taskFilter === 'all' ? 'No tasks yet' : `No ${taskFilter} tasks`}
                </h3>
                <p className="text-neutral-500 mb-6">
                  {taskFilter === 'all' 
                    ? 'Create your first task to see the AI agents in action!'
                    : `No tasks with status "${taskFilter}" found.`
                  }
                </p>
                {taskFilter === 'all' && (
                  <button
                    onClick={() => setActiveTab('create')}
                    className="btn-primary btn-lg"
                  >
                    Create Your First Task
                  </button>
                )}
              </div>
            ) : (
              <div className="grid grid-cols-1 md:grid-cols-2 xl:grid-cols-3 gap-6">
                {filteredTasks.map((task) => (
                  <TaskCard
                    key={task.task_id}
                    task={task}
                    onViewDetails={handleViewTaskDetails}
                    onViewLiveExecution={handleViewLiveExecution}
                  />
                ))}
              </div>
            )}
          </div>
        )}

        {/* Agents Tab */}
        {activeTab === 'agents' && (
          <div className="space-y-6">
            <div>
              <h1 className="text-2xl font-bold text-neutral-900">AI Agents</h1>
              <p className="text-neutral-600 mt-1">Monitor your intelligent agent workforce</p>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 xl:grid-cols-3 gap-6">
              {agents.map((agent, index) => (
                <AgentCard 
                  key={index} 
                  agent={agent} 
                  onViewActivity={handleViewAgentActivity}
                  onViewDetails={(agent) => console.log('View agent details:', agent)}
                />
              ))}
            </div>
          </div>
        )}

        {/* AI Orchestrator Tab */}
        {activeTab === 'create' && (
          <div className="max-w-4xl mx-auto">
            <SmartTaskInput onSubmit={handleCreateTask} isLoading={isLoading} />
          </div>
        )}

        {/* Agent & Task Manager Tab */}
        {activeTab === 'manager' && (
          <AgentTaskManager />
        )}
      </main>

      {/* Modals */}
      <TaskModal
        task={selectedTask}
        isOpen={isModalOpen}
        onClose={() => setIsModalOpen(false)}
      />

      <LiveExecutionMonitor
        taskId={liveMonitorTaskId}
        isVisible={isLiveMonitorOpen}
        onClose={() => {
          setIsLiveMonitorOpen(false);
          setLiveMonitorTaskId(null);
        }}
      />
    </div>
  );
}

export default App;
