import React, { useState, useEffect, useRef } from 'react';
import { 
  Send, 
  Loader2, 
  Sparkles, 
  Lightbulb, 
  Zap,
  ArrowRight,
  CheckCircle2,
  Clock,
  Target,
  Brain
} from 'lucide-react';

const SmartTaskInput = ({ onSubmit, isLoading }) => {
  const [input, setInput] = useState('');
  const [mode, setMode] = useState('task'); // 'task' or 'agent'
  const [suggestions, setSuggestions] = useState([]);
  const [showSuggestions, setShowSuggestions] = useState(false);
  const [selectedSuggestion, setSelectedSuggestion] = useState(-1);
  const [isExpanding, setIsExpanding] = useState(false);
  const [expandedTask, setExpandedTask] = useState(null);
  const [createdAgents, setCreatedAgents] = useState([]);
  const [createdTasks, setCreatedTasks] = useState([]);
  const inputRef = useRef(null);

  // Smart suggestions based on input and mode
  const smartSuggestions = [
    {
      trigger: ['research', 'analyze', 'study'],
      suggestions: mode === 'task' ? [
        'Research market trends in renewable energy',
        'Analyze competitor pricing strategies',
        'Study customer behavior patterns',
        'Research emerging technologies in AI',
        'Analyze social media sentiment'
      ] : [
        'Create a market research specialist',
        'Build an analytical data expert',
        'Design a competitive intelligence agent',
        'Develop a trend analysis specialist',
        'Create a research coordinator'
      ]
    },
    {
      trigger: ['create', 'build', 'develop'],
      suggestions: mode === 'task' ? [
        'Create marketing campaign strategy',
        'Build comprehensive business plan',
        'Develop content calendar',
        'Create competitive analysis report',
        'Build customer onboarding process'
      ] : [
        'Create a strategic planning expert',
        'Build a content creation specialist',
        'Design a business development agent',
        'Develop a creative strategist',
        'Create a project coordinator'
      ]
    },
    {
      trigger: ['optimize', 'improve', 'enhance'],
      suggestions: mode === 'task' ? [
        'Optimize website conversion rates',
        'Improve customer support workflow',
        'Enhance product development process',
        'Optimize supply chain efficiency',
        'Improve team productivity'
      ] : [
        'Create a process optimization expert',
        'Build a performance improvement specialist',
        'Design an efficiency enhancement agent',
        'Develop a workflow optimizer',
        'Create a productivity consultant'
      ]
    },
    {
      trigger: ['monitor', 'track', 'watch'],
      suggestions: mode === 'task' ? [
        'Monitor brand mentions online',
        'Track competitor activities',
        'Watch industry trends',
        'Monitor system performance',
        'Track marketing ROI'
      ] : [
        'Create a monitoring specialist',
        'Build a performance tracking agent',
        'Design a surveillance coordinator',
        'Develop a metrics analyst',
        'Create a quality assurance monitor'
      ]
    }
  ];

  // Quick start templates for both tasks and agents
  const quickTemplates = mode === 'task' ? [
    {
      icon: 'ðŸ”',
      title: 'Market Research',
      description: 'Comprehensive market analysis',
      input: 'research market trends and competitors',
      category: 'research',
      estimatedTime: '2-4 hours'
    },
    {
      icon: 'ðŸ“Š',
      title: 'Business Strategy',
      description: 'Strategic planning and analysis',
      input: 'create business strategy and growth plan',
      category: 'strategy',
      estimatedTime: '4-6 hours'
    },
    {
      icon: 'ðŸ“',
      title: 'Content Creation',
      description: 'Marketing content development',
      input: 'develop content marketing strategy',
      category: 'content',
      estimatedTime: '3-5 hours'
    },
    {
      icon: 'âš¡',
      title: 'Process Optimization',
      description: 'Workflow improvement',
      input: 'optimize business processes and workflows',
      category: 'optimization',
      estimatedTime: '4-8 hours'
    },
    {
      icon: 'ðŸ“ˆ',
      title: 'Performance Analysis',
      description: 'Metrics and KPI tracking',
      input: 'analyze performance metrics and KPIs',
      category: 'analysis',
      estimatedTime: '2-3 hours'
    },
    {
      icon: 'ðŸŽ¯',
      title: 'Competitive Intelligence',
      description: 'Competitor analysis',
      input: 'research competitors and market positioning',
      category: 'intelligence',
      estimatedTime: '3-6 hours'
    }
  ] : [
    {
      icon: 'ðŸ§ ',
      title: 'Research Specialist',
      description: 'Expert in data gathering and analysis',
      input: 'analytical research expert with market intelligence capabilities',
      category: 'research',
      estimatedTime: 'Instant'
    },
    {
      icon: 'ðŸŽ¨',
      title: 'Creative Strategist',
      description: 'Innovation and content creation',
      input: 'creative strategist for marketing and content development',
      category: 'creative',
      estimatedTime: 'Instant'
    },
    {
      icon: 'ðŸ“‹',
      title: 'Project Coordinator',
      description: 'Task management and orchestration',
      input: 'project coordination specialist with team leadership skills',
      category: 'coordination',
      estimatedTime: 'Instant'
    },
    {
      icon: 'ðŸ”§',
      title: 'Process Optimizer',
      description: 'Efficiency and workflow expert',
      input: 'process optimization expert focused on efficiency improvements',
      category: 'optimization',
      estimatedTime: 'Instant'
    },
    {
      icon: 'ðŸ“Š',
      title: 'Data Analyst',
      description: 'Statistical analysis and insights',
      input: 'data analysis specialist with statistical modeling expertise',
      category: 'analysis',
      estimatedTime: 'Instant'
    },
    {
      icon: 'ðŸ›¡ï¸',
      title: 'Quality Assurance',
      description: 'Standards and validation expert',
      input: 'quality assurance specialist with validation and compliance focus',
      category: 'quality',
      estimatedTime: 'Instant'
    }
  ];

  useEffect(() => {
    if (input.length > 2) {
      const matchingSuggestions = [];

      smartSuggestions.forEach(group => {
        const hasMatch = group.trigger.some(trigger => 
          input.toLowerCase().includes(trigger)
        );

        if (hasMatch) {
          matchingSuggestions.push(...group.suggestions);
        }
      });

      // Add fuzzy matching for partial inputs
      if (matchingSuggestions.length === 0) {
        const allSuggestions = smartSuggestions.flatMap(g => g.suggestions);
        const fuzzyMatches = allSuggestions.filter(suggestion =>
          suggestion.toLowerCase().includes(input.toLowerCase()) ||
          input.toLowerCase().split(' ').some(word => 
            suggestion.toLowerCase().includes(word) && word.length > 2
          )
        );
        matchingSuggestions.push(...fuzzyMatches);
      }

      setSuggestions(matchingSuggestions.slice(0, 5));
      setShowSuggestions(matchingSuggestions.length > 0);
    } else {
      setShowSuggestions(false);
    }
  }, [input]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (input.trim() && !isLoading) {
      setIsExpanding(true);

      if (mode === 'agent') {
        // Handle agent creation
        try {
          const response = await fetch('/api/agents/create', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify({
              description: input
            })
          });

          if (response.ok) {
            const agentData = await response.json();
            setCreatedAgents(prev => [...prev, agentData]);

            setExpandedTask({
              type: 'agent',
              originalInput: input,
              expandedDescription: `Created AI agent "${agentData.name}" with role: ${agentData.role}. This agent specializes in ${agentData.specializations.join(', ')} and uses ${agentData.collaboration_style} collaboration style.`,
              agentDetails: agentData
            });
          }
        } catch (error) {
          console.error('Failed to create agent:', error);
        }
      } else {
        // Handle task creation/execution (existing logic)
        const mockExpansion = {
          type: 'task',
          originalInput: input,
          expandedDescription: `Comprehensive ${input} with detailed analysis, strategic recommendations, and actionable insights. This task will involve multi-agent collaboration to ensure thorough coverage and high-quality deliverables.`,
          estimatedDuration: '3-6 hours',
          requiredAgents: 4,
          subtasks: 8,
          expectedOutputs: ['Executive Summary', 'Detailed Report', 'Recommendations', 'Action Plan']
        };

        setExpandedTask(mockExpansion);
      }

      setIsExpanding(false);

      // Auto-submit after showing expansion
      setTimeout(() => {
        if (mode === 'task') {
          onSubmit(input);
        }
        setInput('');
        setExpandedTask(null);
      }, mode === 'agent' ? 3000 : 2000);
    }
  };

  const handleSuggestionClick = (suggestion) => {
    setInput(suggestion);
    setShowSuggestions(false);
    inputRef.current?.focus();
  };

  const handleTemplateClick = (template) => {
    setInput(template.input);
    inputRef.current?.focus();
  };

  const handleKeyDown = (e) => {
    if (showSuggestions) {
      if (e.key === 'ArrowDown') {
        e.preventDefault();
        setSelectedSuggestion(prev => 
          prev < suggestions.length - 1 ? prev + 1 : prev
        );
      } else if (e.key === 'ArrowUp') {
        e.preventDefault();
        setSelectedSuggestion(prev => prev > 0 ? prev - 1 : prev);
      } else if (e.key === 'Enter' && selectedSuggestion >= 0) {
        e.preventDefault();
        handleSuggestionClick(suggestions[selectedSuggestion]);
      } else if (e.key === 'Escape') {
        setShowSuggestions(false);
        setSelectedSuggestion(-1);
      }
    }
  };

  return (
    <div className="space-y-6">
      {/* Main Input Section */}
      <div className="card p-6">
        <div className="flex items-center justify-between mb-6">
          <div className="flex items-center space-x-3">
            <div className="p-2 bg-gradient-to-br from-brand-500 to-brand-600 rounded-lg">
              <Brain className="w-5 h-5 text-white" />
            </div>
            <div>
              <h2 className="text-xl font-semibold text-neutral-900">AI Orchestrator</h2>
              <p className="text-sm text-neutral-600">
                {mode === 'task' ? 'Describe what you need - AI handles the rest' : 'Describe the agent you need - AI creates it'}
              </p>
            </div>
          </div>

          {/* Mode Toggle */}
          <div className="flex bg-neutral-100 rounded-lg p-1">
            <button
              onClick={() => setMode('task')}
              className={`px-4 py-2 rounded-md text-sm font-medium transition-all ${
                mode === 'task' 
                  ? 'bg-white text-brand-700 shadow-sm' 
                  : 'text-neutral-600 hover:text-neutral-800'
              }`}
            >
              Create Task
            </button>
            <button
              onClick={() => setMode('agent')}
              className={`px-4 py-2 rounded-md text-sm font-medium transition-all ${
                mode === 'agent' 
                  ? 'bg-white text-brand-700 shadow-sm' 
                  : 'text-neutral-600 hover:text-neutral-800'
              }`}
            >
              Create Agent
            </button>
          </div>
        </div>

        <form onSubmit={handleSubmit} className="space-y-4">
          <div className="relative">
            <div className="relative">
              <input
                ref={inputRef}
                type="text"
                value={input}
                onChange={(e) => setInput(e.target.value)}
                onKeyDown={handleKeyDown}
                placeholder={mode === 'task' 
                  ? "What would you like to accomplish? (e.g., 'research AI trends', 'create marketing plan')"
                  : "What kind of agent do you need? (e.g., 'marketing expert', 'data analyst', 'project coordinator')"
                }
                className="w-full px-4 py-4 text-lg border border-neutral-300 rounded-xl shadow-sm focus:border-brand-500 focus:ring-brand-500 pr-16"
                disabled={isLoading || isExpanding}
              />

              <button
                type="submit"
                disabled={!input.trim() || isLoading || isExpanding}
                className="absolute right-2 top-1/2 transform -translate-y-1/2 btn-primary btn-sm"
              >
                {isLoading || isExpanding ? (
                  <Loader2 className="w-4 h-4 animate-spin" />
                ) : (
                  <Send className="w-4 h-4" />
                )}
              </button>
            </div>

            {/* Smart Suggestions Dropdown */}
            {showSuggestions && (
              <div className="absolute top-full left-0 right-0 mt-2 bg-white border border-neutral-200 rounded-lg shadow-strong z-50 max-h-60 overflow-y-auto">
                {suggestions.map((suggestion, index) => (
                  <button
                    key={index}
                    onClick={() => handleSuggestionClick(suggestion)}
                    className={`w-full text-left px-4 py-3 hover:bg-neutral-50 transition-colors border-b border-neutral-100 last:border-b-0 ${
                      index === selectedSuggestion ? 'bg-brand-50 text-brand-700' : 'text-neutral-700'
                    }`}
                  >
                    <div className="flex items-center space-x-2">
                      <Lightbulb className="w-4 h-4 text-neutral-400" />
                      <span>{suggestion}</span>
                    </div>
                  </button>
                ))}
              </div>
            )}
          </div>

          {/* AI Expansion Preview */}
          {isExpanding && (
            <div className="p-4 bg-gradient-to-r from-brand-50 to-info-50 rounded-lg border border-brand-200 animate-fade-in">
              <div className="flex items-center space-x-2 mb-3">
                <Sparkles className="w-5 h-5 text-brand-600 animate-pulse" />
                <span className="text-sm font-medium text-brand-700">
                  {mode === 'task' ? 'AI is expanding your task...' : 'AI is creating your agent...'}
                </span>
              </div>
              <div className="space-y-2 text-sm text-brand-600">
                <div className="flex items-center space-x-2">
                  <div className="w-2 h-2 bg-brand-400 rounded-full animate-pulse"></div>
                  <span>
                    {mode === 'task' ? 'Analyzing task requirements' : 'Analyzing agent requirements'}
                  </span>
                </div>
                <div className="flex items-center space-x-2">
                  <div className="w-2 h-2 bg-brand-400 rounded-full animate-pulse" style={{ animationDelay: '0.2s' }}></div>
                  <span>
                    {mode === 'task' ? 'Selecting optimal agents' : 'Defining role and capabilities'}
                  </span>
                </div>
                <div className="flex items-center space-x-2">
                  <div className="w-2 h-2 bg-brand-400 rounded-full animate-pulse" style={{ animationDelay: '0.4s' }}></div>
                  <span>
                    {mode === 'task' ? 'Creating execution plan' : 'Generating backstory and tools'}
                  </span>
                </div>
              </div>
            </div>
          )}

          {/* Expanded Task/Agent Preview */}
          {expandedTask && (
            <div className="p-4 bg-success-50 rounded-lg border border-success-200 animate-slide-up">
              <div className="flex items-center space-x-2 mb-3">
                <CheckCircle2 className="w-5 h-5 text-success-600" />
                <span className="text-sm font-medium text-success-700">
                  {expandedTask.type === 'agent' ? 'Agent Created Successfully!' : 'Task Expanded Successfully!'}
                </span>
              </div>

              {expandedTask.type === 'agent' ? (
                <div className="space-y-3">
                  <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
                    <div className="text-center">
                      <div className="text-lg font-bold text-success-700">{expandedTask.agentDetails?.name}</div>
                      <div className="text-xs text-success-600">Agent Name</div>
                    </div>
                    <div className="text-center">
                      <div className="text-lg font-bold text-success-700">{expandedTask.agentDetails?.role}</div>
                      <div className="text-xs text-success-600">Role</div>
                    </div>
                    <div className="text-center">
                      <div className="text-lg font-bold text-success-700">{expandedTask.agentDetails?.expertise_level}</div>
                      <div className="text-xs text-success-600">Expertise</div>
                    </div>
                    <div className="text-center">
                      <div className="text-lg font-bold text-success-700">{expandedTask.agentDetails?.tools?.length || 0}</div>
                      <div className="text-xs text-success-600">Tools</div>
                    </div>
                  </div>

                  <p className="text-sm text-success-700">
                    {expandedTask.expandedDescription}
                  </p>

                  <div className="flex items-center space-x-2 text-xs text-success-600">
                    <Zap className="w-3 h-3" />
                    <span>Agent ready for task assignment...</span>
                  </div>
                </div>
              ) : (
                <div>
                  <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-4">
                    <div className="text-center">
                      <div className="text-lg font-bold text-success-700">{expandedTask.requiredAgents}</div>
                      <div className="text-xs text-success-600">AI Agents</div>
                    </div>
                    <div className="text-center">
                      <div className="text-lg font-bold text-success-700">{expandedTask.subtasks}</div>
                      <div className="text-xs text-success-600">Subtasks</div>
                    </div>
                    <div className="text-center">
                      <div className="text-lg font-bold text-success-700">{expandedTask.estimatedDuration}</div>
                      <div className="text-xs text-success-600">Duration</div>
                    </div>
                    <div className="text-center">
                      <div className="text-lg font-bold text-success-700">{expandedTask.expectedOutputs?.length || 0}</div>
                      <div className="text-xs text-success-600">Deliverables</div>
                    </div>
                  </div>

                  <p className="text-sm text-success-700 mb-3">
                    {expandedTask.expandedDescription}
                  </p>

                  <div className="flex items-center space-x-2 text-xs text-success-600">
                    <Zap className="w-3 h-3" />
                    <span>Launching multi-agent orchestration...</span>
                  </div>
                </div>
              )}
            </div>
          )}
        </form>
      </div>

      {/* Quick Templates */}
      <div className="card p-6">
        <h3 className="text-lg font-semibold text-neutral-900 mb-4 flex items-center">
          <Target className="w-5 h-5 mr-2" />
          {mode === 'task' ? 'Quick Start Templates' : 'Agent Templates'}
        </h3>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          {quickTemplates.map((template, index) => (
            <button
              key={index}
              onClick={() => handleTemplateClick(template)}
              disabled={isLoading || isExpanding}
              className="p-4 text-left bg-neutral-50 hover:bg-neutral-100 rounded-lg transition-all duration-200 group border border-transparent hover:border-brand-200"
            >
              <div className="flex items-start space-x-3">
                <div className="text-2xl">{template.icon}</div>
                <div className="flex-1 min-w-0">
                  <h4 className="font-medium text-neutral-900 group-hover:text-brand-700 transition-colors">
                    {template.title}
                  </h4>
                  <p className="text-sm text-neutral-600 mb-2 line-clamp-2">
                    {template.description}
                  </p>
                  <div className="flex items-center justify-between">
                    <div className="flex items-center space-x-1 text-xs text-neutral-500">
                      <Clock className="w-3 h-3" />
                      <span>{template.estimatedTime}</span>
                    </div>
                    <ArrowRight className="w-4 h-4 text-neutral-400 group-hover:text-brand-500 transition-colors" />
                  </div>
                </div>
              </div>
            </button>
          ))}
        </div>
      </div>

      {/* AI Capabilities Info */}
      <div className="card p-6 bg-gradient-to-r from-neutral-50 to-brand-50">
        <h3 className="text-lg font-semibold text-neutral-900 mb-4">ðŸ¤– AI Orchestration Capabilities</h3>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          {mode === 'task' ? (
            <>
              <div>
                <h4 className="font-medium text-neutral-800 mb-2">Automatic Task Expansion</h4>
                <ul className="text-sm text-neutral-600 space-y-1">
                  <li>â€¢ Converts simple inputs into comprehensive specifications</li>
                  <li>â€¢ Identifies required resources and dependencies</li>
                  <li>â€¢ Creates detailed execution plans automatically</li>
                </ul>
              </div>

              <div>
                <h4 className="font-medium text-neutral-800 mb-2">Intelligent Agent Selection</h4>
                <ul className="text-sm text-neutral-600 space-y-1">
                  <li>â€¢ Automatically selects optimal AI agents for each task</li>
                  <li>â€¢ Balances workload across available resources</li>
                  <li>â€¢ Ensures quality through multi-agent collaboration</li>
                </ul>
              </div>
            </>
          ) : (
            <>
              <div>
                <h4 className="font-medium text-neutral-800 mb-2">Intelligent Agent Creation</h4>
                <ul className="text-sm text-neutral-600 space-y-1">
                  <li>â€¢ Generates comprehensive agent profiles from simple descriptions</li>
                  <li>â€¢ Automatically assigns appropriate tools and capabilities</li>
                  <li>â€¢ Creates detailed backstories and specializations</li>
                </ul>
              </div>

              <div>
                <h4 className="font-medium text-neutral-800 mb-2">Smart Role Assignment</h4>
                <ul className="text-sm text-neutral-600 space-y-1">
                  <li>â€¢ Determines optimal collaboration styles</li>
                  <li>â€¢ Assigns expertise levels and performance metrics</li>
                  <li>â€¢ Configures LLM parameters for best results</li>
                </ul>
              </div>
            </>
          )}
        </div>

        {/* Created Agents/Tasks Summary */}
        {((mode === 'agent' && createdAgents.length > 0) || (mode === 'task' && createdTasks.length > 0)) && (
          <div className="mt-6 pt-4 border-t border-neutral-200">
            <h4 className="font-medium text-neutral-800 mb-2">
              {mode === 'agent' ? `Created Agents (${createdAgents.length})` : `Created Tasks (${createdTasks.length})`}
            </h4>
            <div className="flex flex-wrap gap-2">
              {mode === 'agent' 
                ? createdAgents.slice(-3).map((agent, index) => (
                    <span key={index} className="px-3 py-1 bg-brand-100 text-brand-700 rounded-full text-xs">
                      {agent.name} - {agent.role}
                    </span>
                  ))
                : createdTasks.slice(-3).map((task, index) => (
                    <span key={index} className="px-3 py-1 bg-info-100 text-info-700 rounded-full text-xs">
                      {task.name}
                    </span>
                  ))
              }
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default SmartTaskInput;
