import React, { useState, useEffect } from 'react';
import Header from './components/Header';
import AgentCard from './components/AgentCard';
import TaskForm from './components/TaskForm';
import TaskCard from './components/TaskCard';
import TaskModal from './components/TaskModal';
import LiveExecutionMonitor from './components/LiveExecutionMonitor';
import { apiService, wsService } from './services/api';
import { useWebSocket } from './hooks/useWebSocket';
import { RefreshCw, Users, ListTodo, Activity } from 'lucide-react';

function App() {
  const [agents, setAgents] = useState([]);
  const [tasks, setTasks] = useState([]);
  const [selectedTask, setSelectedTask] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isLiveMonitorOpen, setIsLiveMonitorOpen] = useState(false);
  const [liveMonitorTaskId, setLiveMonitorTaskId] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [isRefreshing, setIsRefreshing] = useState(false);

  const { isConnected, messages } = useWebSocket();

  // Load initial data
  useEffect(() => {
    loadAgents();
    loadTasks();
  }, []);

  // Handle WebSocket messages
  useEffect(() => {
    const latestMessage = messages[messages.length - 1];
    if (latestMessage) {
      switch (latestMessage.type) {
        case 'task_created':
        case 'task_started':
        case 'task_completed':
        case 'task_failed':
          loadTasks();
          break;
        case 'agent_heartbeat':
        case 'agent_statuses':
          if (latestMessage.agents) {
            setAgents(latestMessage.agents);
          }
          break;
        case 'execution_log':
          // Update task progress in real-time
          setTasks(prevTasks => 
            prevTasks.map(task => 
              task.task_id === latestMessage.task_id 
                ? {
                    ...task,
                    progress: latestMessage.task_progress || task.progress,
                    current_agent: latestMessage.current_agent,
                    current_step: latestMessage.current_step
                  }
                : task
            )
          );
          break;
        default:
          break;
      }
    }
  }, [messages]);

  // Set up WebSocket service reference for components
  useEffect(() => {
    window.wsService = wsService;
  }, []);

  const loadAgents = async () => {
    try {
      const response = await apiService.getAgents();
      setAgents(response.data.agents);
    } catch (error) {
      console.error('Error loading agents:', error);
    }
  };

  const loadTasks = async () => {
    try {
      const response = await apiService.getAllTasks();
      setTasks(response.data.tasks.sort((a, b) => 
        new Date(b.created_at) - new Date(a.created_at)
      ));
    } catch (error) {
      console.error('Error loading tasks:', error);
    }
  };

  const handleCreateTask = async (taskDescription) => {
    setIsLoading(true);
    try {
      const response = await apiService.createTask(taskDescription);
      // Automatically open live monitor for new running tasks
      setLiveMonitorTaskId(response.data.task_id);
      setIsLiveMonitorOpen(true);
    } catch (error) {
      console.error('Error creating task:', error);
      alert('Failed to create task. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  const handleViewTaskDetails = (task) => {
    setSelectedTask(task);
    setIsModalOpen(true);
  };

  const handleViewLiveExecution = (task) => {
    setLiveMonitorTaskId(task.task_id);
    setIsLiveMonitorOpen(true);
  };

  const handleViewAgentActivity = (agent) => {
    // Find the current task for this agent
    const currentTask = tasks.find(task => 
      task.status === 'running' && task.current_agent === agent.name
    );

    if (currentTask) {
      handleViewLiveExecution(currentTask);
    }
  };

  const handleRefresh = async () => {
    setIsRefreshing(true);
    await Promise.all([loadAgents(), loadTasks()]);
    setIsRefreshing(false);
  };

  const getTaskStats = () => {
    const total = tasks.length;
    const completed = tasks.filter(t => t.status === 'completed').length;
    const running = tasks.filter(t => t.status === 'running').length;
    const failed = tasks.filter(t => t.status === 'failed').length;

    return { total, completed, running, failed };
  };

  const getAgentStats = () => {
    const total = agents.length;
    const ready = agents.filter(a => a.status === 'ready').length;
    const busy = agents.filter(a => a.status === 'busy').length;
    const totalCompleted = agents.reduce((sum, agent) => sum + (agent.total_tasks_completed || 0), 0);

    return { total, ready, busy, totalCompleted };
  };

  const stats = getTaskStats();
  const agentStats = getAgentStats();

  return (
    <div className="min-h-screen bg-gray-50">
      <Header isConnected={isConnected} />

      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          {/* Left Column - Task Creation & Stats */}
          <div className="lg:col-span-1">
            <TaskForm onSubmit={handleCreateTask} isLoading={isLoading} />

            {/* Task Stats */}
            <div className="mt-6 bg-white rounded-lg border border-gray-200 p-4">
              <div className="flex items-center justify-between mb-4">
                <h3 className="text-lg font-semibold text-gray-900 flex items-center">
                  <ListTodo className="w-5 h-5 mr-2" />
                  Task Statistics
                </h3>
                <button
                  onClick={handleRefresh}
                  disabled={isRefreshing}
                  className="p-2 text-gray-400 hover:text-gray-600 transition-colors"
                  title="Refresh data"
                >
                  <RefreshCw className={`w-4 h-4 ${isRefreshing ? 'animate-spin' : ''}`} />
                </button>
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div className="text-center">
                  <div className="text-2xl font-bold text-gray-900">{stats.total}</div>
                  <div className="text-sm text-gray-500">Total</div>
                </div>
                <div className="text-center">
                  <div className="text-2xl font-bold text-success-600">{stats.completed}</div>
                  <div className="text-sm text-gray-500">Completed</div>
                </div>
                <div className="text-center">
                  <div className="text-2xl font-bold text-warning-600">{stats.running}</div>
                  <div className="text-sm text-gray-500">Running</div>
                </div>
                <div className="text-center">
                  <div className="text-2xl font-bold text-error-600">{stats.failed}</div>
                  <div className="text-sm text-gray-500">Failed</div>
                </div>
              </div>
            </div>

            {/* Agent Stats */}
            <div className="mt-6 bg-white rounded-lg border border-gray-200 p-4">
              <h3 className="text-lg font-semibold text-gray-900 mb-4 flex items-center">
                <Activity className="w-5 h-5 mr-2" />
                Agent Performance
              </h3>

              <div className="space-y-3">
                <div className="flex justify-between items-center">
                  <span className="text-sm text-gray-600">Active Agents</span>
                  <span className="text-sm font-semibold text-gray-900">
                    {agentStats.busy}/{agentStats.total}
                  </span>
                </div>

                <div className="flex justify-between items-center">
                  <span className="text-sm text-gray-600">Ready Agents</span>
                  <span className="text-sm font-semibold text-success-600">
                    {agentStats.ready}
                  </span>
                </div>

                <div className="flex justify-between items-center">
                  <span className="text-sm text-gray-600">Total Tasks Completed</span>
                  <span className="text-sm font-semibold text-primary-600">
                    {agentStats.totalCompleted}
                  </span>
                </div>

                {agentStats.busy > 0 && (
                  <div className="mt-3 p-2 bg-warning-50 rounded border border-warning-200">
                    <div className="flex items-center space-x-2">
                      <Activity className="w-4 h-4 text-warning-600 animate-pulse" />
                      <span className="text-sm text-warning-700 font-medium">
                        {agentStats.busy} agent{agentStats.busy > 1 ? 's' : ''} currently processing
                      </span>
                    </div>
                  </div>
                )}
              </div>
            </div>
          </div>

          {/* Right Column - Agents and Tasks */}
          <div className="lg:col-span-2 space-y-8">
            {/* Agents Section */}
            <div>
              <h2 className="text-xl font-semibold text-gray-900 mb-4 flex items-center">
                <Users className="w-5 h-5 mr-2" />
                AI Agents ({agents.length})
              </h2>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                {agents.map((agent, index) => (
                  <AgentCard 
                    key={index} 
                    agent={agent} 
                    onViewActivity={handleViewAgentActivity}
                  />
                ))}
              </div>
            </div>

            {/* Tasks Section */}
            <div>
              <h2 className="text-xl font-semibold text-gray-900 mb-4 flex items-center">
                <ListTodo className="w-5 h-5 mr-2" />
                Recent Tasks ({tasks.length})
              </h2>

              {tasks.length === 0 ? (
                <div className="bg-white rounded-lg border border-gray-200 p-8 text-center">
                  <ListTodo className="w-12 h-12 text-gray-400 mx-auto mb-4" />
                  <h3 className="text-lg font-medium text-gray-900 mb-2">No tasks yet</h3>
                  <p className="text-gray-500">
                    Create your first task to see the AI agents in action!
                  </p>
                </div>
              ) : (
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  {tasks.map((task) => (
                    <TaskCard
                      key={task.task_id}
                      task={task}
                      onViewDetails={handleViewTaskDetails}
                      onViewLiveExecution={handleViewLiveExecution}
                    />
                  ))}
                </div>
              )}
            </div>
          </div>
        </div>
      </main>

      {/* Task Details Modal */}
      <TaskModal
        task={selectedTask}
        isOpen={isModalOpen}
        onClose={() => setIsModalOpen(false)}
      />

      {/* Live Execution Monitor */}
      <LiveExecutionMonitor
        taskId={liveMonitorTaskId}
        isVisible={isLiveMonitorOpen}
        onClose={() => {
          setIsLiveMonitorOpen(false);
          setLiveMonitorTaskId(null);
        }}
      />
    </div>
  );
}

export default App;
