import React, { useState, useRef } from 'react';
import { Send, Loader2, Sparkles, Zap, MessageSquare } from 'lucide-react';

const ChatBox = ({ onSendMessage, onExecutionStart }) => {
  const [message, setMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [isFocused, setIsFocused] = useState(false);
  const textareaRef = useRef(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!message.trim() || isLoading) return;

    const currentMessage = message;
    setMessage('');
    setIsLoading(true);

    try {
      // Check if message is a task execution request
      const isTaskExecution = currentMessage.toLowerCase().includes('execute') || 
                             currentMessage.toLowerCase().includes('run') ||
                             currentMessage.toLowerCase().includes('start task') ||
                             currentMessage.toLowerCase().includes('create');

      if (isTaskExecution) {
        onExecutionStart({
          task_name: currentMessage,
          agent_name: 'AI Assistant',
          description: 'Processing user request...',
          timestamp: new Date()
        });
      }

      await onSendMessage(currentMessage);
    } catch (error) {
      console.error('Error sending message:', error);
      // Restore message on error
      setMessage(currentMessage);
    } finally {
      setIsLoading(false);
    }
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSubmit(e);
    }
  };

  const handleQuickAction = (action) => {
    setMessage(action);
    textareaRef.current?.focus();
  };

  const quickActions = [
    { 
      label: 'Create Agent', 
      action: 'Create a new intelligent agent for data analysis and processing',
      icon: <Sparkles className="w-3 h-3" />,
      color: 'bg-purple-50 text-purple-700 border-purple-200 hover:bg-purple-100'
    },
    { 
      label: 'View Tasks', 
      action: 'Show me all currently running and completed tasks with their status',
      icon: <MessageSquare className="w-3 h-3" />,
      color: 'bg-blue-50 text-blue-700 border-blue-200 hover:bg-blue-100'
    },
    { 
      label: 'Execute Task', 
      action: 'Execute a comprehensive web scraping and data analysis task',
      icon: <Zap className="w-3 h-3" />,
      color: 'bg-green-50 text-green-700 border-green-200 hover:bg-green-100'
    }
  ];

  return (
    <div className="flex flex-col h-full bg-white border-t border-neutral-200">
      {/* Enhanced Header */}
      <div className="px-6 py-4 border-b border-neutral-100 bg-gradient-to-r from-neutral-50 to-white">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-3">
            <div className="relative">
              <div className="w-3 h-3 bg-success-500 rounded-full animate-pulse"></div>
              <div className="absolute inset-0 w-3 h-3 bg-success-500 rounded-full animate-ping opacity-75"></div>
            </div>
            <div>
              <span className="text-sm font-semibold text-neutral-800">AI Assistant</span>
              <p className="text-xs text-neutral-500">Ready to help with your tasks</p>
            </div>
          </div>
          <div className="flex items-center space-x-2 text-xs text-neutral-400">
            <span>Press Enter to send</span>
            <kbd className="px-2 py-1 bg-neutral-100 rounded text-neutral-600 font-mono">‚èé</kbd>
          </div>
        </div>
      </div>

      {/* Enhanced Input Area */}
      <div className="flex-1 p-6">
        <form onSubmit={handleSubmit} className="space-y-4">
          <div className={`relative transition-all duration-200 ${isFocused ? 'transform scale-[1.02]' : ''}`}>
            <textarea
              ref={textareaRef}
              value={message}
              onChange={(e) => setMessage(e.target.value)}
              onKeyDown={handleKeyDown}
              onFocus={() => setIsFocused(true)}
              onBlur={() => setIsFocused(false)}
              placeholder="Describe your task or ask a question... (Shift+Enter for new line)"
              className={`w-full px-4 py-3 text-sm border rounded-xl focus:outline-none focus:ring-2 focus:ring-brand-500 focus:border-transparent resize-none transition-all duration-200 ${
                isFocused 
                  ? 'border-brand-300 shadow-lg bg-white' 
                  : 'border-neutral-300 shadow-sm bg-neutral-50'
              } ${isLoading ? 'opacity-75' : ''}`}
              rows="3"
              disabled={isLoading}
              maxLength={1000}
            />

            {/* Character Counter */}
            <div className="absolute bottom-2 left-4 text-xs text-neutral-400">
              {message.length}/1000
            </div>

            {/* Send Button - Enhanced */}
            <button
              type="submit"
              disabled={!message.trim() || isLoading}
              className={`absolute bottom-2 right-2 px-4 py-2 rounded-lg font-medium text-sm transition-all duration-200 flex items-center space-x-2 ${
                !message.trim() || isLoading
                  ? 'bg-neutral-100 text-neutral-400 cursor-not-allowed'
                  : 'bg-brand-600 text-white hover:bg-brand-700 hover:shadow-md active:scale-95 shadow-sm'
              }`}
            >
              {isLoading ? (
                <>
                  <Loader2 className="w-4 h-4 animate-spin" />
                  <span>Sending...</span>
                </>
              ) : (
                <>
                  <Send className="w-4 h-4" />
                  <span>Send</span>
                </>
              )}
            </button>
          </div>
        </form>

        {/* Enhanced Quick Actions */}
        <div className="mt-4">
          <p className="text-xs font-medium text-neutral-600 mb-3">Quick Actions</p>
          <div className="flex flex-wrap gap-2">
            {quickActions.map((action, index) => (
              <button
                key={index}
                onClick={() => handleQuickAction(action.action)}
                className={`px-3 py-2 text-xs font-medium rounded-lg border transition-all duration-200 flex items-center space-x-2 hover:shadow-sm active:scale-95 ${action.color}`}
                disabled={isLoading}
                title={action.action}
              >
                {action.icon}
                <span>{action.label}</span>
              </button>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default ChatBox;
