import React, { useState } from 'react';
import { Send, Loader2 } from 'lucide-react';

const TaskForm = ({ onSubmit, isLoading }) => {
  const [taskDescription, setTaskDescription] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (taskDescription.trim() && !isLoading) {
      onSubmit(taskDescription.trim());
      setTaskDescription('');
    }
  };

  const exampleTasks = [
    "Research the latest trends in artificial intelligence and machine learning",
    "Analyze market data for renewable energy companies in 2024",
    "Create a comprehensive project plan for implementing a new CRM system",
    "Gather competitive intelligence on pricing strategies in the SaaS industry",
    "Develop a content strategy for social media marketing campaign"
  ];

  const handleExampleClick = (example) => {
    setTaskDescription(example);
  };

  return (
    <div className="bg-white rounded-lg border border-gray-200 p-6">
      <h2 className="text-lg font-semibold text-gray-900 mb-4">
        Create New Task
      </h2>

      <form onSubmit={handleSubmit} className="space-y-4">
        <div>
          <label htmlFor="task-description" className="block text-sm font-medium text-gray-700 mb-2">
            Task Description
          </label>
          <textarea
            id="task-description"
            rows={4}
            className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500 resize-none"
            placeholder="Describe the task you want the AI agents to work on..."
            value={taskDescription}
            onChange={(e) => setTaskDescription(e.target.value)}
            disabled={isLoading}
          />
        </div>

        <div className="flex justify-between items-center">
          <span className="text-sm text-gray-500">
            {taskDescription.length}/1000 characters
          </span>

          <button
            type="submit"
            disabled={!taskDescription.trim() || isLoading}
            className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-primary-600 hover:bg-primary-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500 disabled:opacity-50 disabled:cursor-not-allowed"
          >
            {isLoading ? (
              <>
                <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                Processing...
              </>
            ) : (
              <>
                <Send className="w-4 h-4 mr-2" />
                Execute Task
              </>
            )}
          </button>
        </div>
      </form>

      <div className="mt-6">
        <h3 className="text-sm font-medium text-gray-700 mb-3">Example Tasks:</h3>
        <div className="space-y-2">
          {exampleTasks.map((example, index) => (
            <button
              key={index}
              onClick={() => handleExampleClick(example)}
              className="w-full text-left px-3 py-2 text-sm text-gray-600 bg-gray-50 rounded-md hover:bg-gray-100 transition-colors"
              disabled={isLoading}
            >
              {example}
            </button>
          ))}
        </div>
      </div>
    </div>
  );
};

export default TaskForm;
